import { useI18n } from 'vue-i18n';
import router from "@/router";
const { t, locale } = useI18n();

const counter  = ref(0);

const increment = ()=>{
    counter.value += 1
    if(counter.value === 2){
        redirectToPradiZone()
    }
}

const redirectToPradiZone =  ()=>{
    const token = localStorage.getItem('token');
    if(!token){
        router.push({name:'pradi'});
    }else{
        router.push({name:'zone'});
    }

}

