<style scoped>
h1{
    font-size: 4.5em;
    color: var(--primary-500);
}
.contenedor{
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100vh;
    gap: 2em;
}
.contenedor-sobremi{
    display: flex;
    width: 100%;
    justify-content: space-around;
    align-items: center;
    gap: 3em;


}
.texto{
    display: flex;
    flex-direction: column;
    gap: 2em;
    width: 70%;
}
.texto p{
    font-size: 1.5em;
}
.perfil{
    background-color: gray;
    height: 15em;
    width: 15em;
    border-radius: 100%;
}
.contenedor-habilidades{
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 50px;
}
.iconos1,.iconos2{
    display: flex;
    align-items: center;
    gap: 2em;
}
hr{
    width: 80vh;
    height: 0.5em;
    border-radius: 10px;
    background-color: var(--primary-500);
}
</style>